<template><div><button @click='open=true' :style='btn'>Open Drawer</button><div :style='drawer(open)'><div :style='bar'><div>Terminal</div><button @click='open=false' :style='btnSmall' aria-label='Close'>âœ•</button></div><div :style='body'><pre class='small' style='margin:0;color:#9aa0b6'>$ echo "Hello, world!"</pre></div></div></div></template><script setup lang='ts'>import { ref } from 'vue'; const open=ref(false); const btn={background:'#101632',color:'#e5e7f5',border:'1px solid #2b3253',borderRadius:'8px',padding:'8px 10px',cursor:'pointer'}; const btnSmall={background:'transparent',color:'#c4ccff',border:'1px solid #2b3253',borderRadius:'8px',padding:'4px 8px',cursor:'pointer'}; const bar={display:'flex',justifyContent:'space-between',alignItems:'center',padding:'8px 12px',borderBottom:'1px solid #2b3253',color:'#c4ccff'}; const body={padding:'12px',height:'calc(40vh - 42px)',overflow:'auto'}; const drawer=(open:boolean)=>({position:'fixed',left:0,right:0,bottom:0,height:'40vh',background:'#0f1324',borderTop:'1px solid #2b3253',transition:'transform 200ms ease',zIndex:50,boxShadow:'0 -10px 30px rgba(0,0,0,0.4)',transform:open?'translateY(0)':'translateY(100%)'})</script>